var link=document.querySelector(".contacts-email"),popup=document.querySelector(".modal-email"),close=popup.querySelector(".modal-email-close"),username=popup.querySelector("[name=username]"),form=popup.querySelector("form"),email=popup.querySelector("[name=email]"),storage=localStorage.getItem("username");link.addEventListener("click",function(a){a.preventDefault();popup.classList.add("modal-email-show");storage?(username.value=storage,email.focus()):username.focus()});
close.addEventListener("click",function(a){a.preventDefault();popup.classList.remove("modal-email-show");popup.classList.remove("modal-error")});form.addEventListener("submit",function(a){username.value&&email.value?localStorage.setItem("username",username.value):(a.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))});
window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal-email-show")&&(popup.classList.remove("modal-email-show"),popup.classList.remove("modal-error"))});